mapper.select:
select
  MSG_ID, MSG_SENDER, MSG_RECEIVER, SEND_TIME, MSG, MSG_TYPE, MSG_PATH, IS_DELETE, IS_IMPORTANT, LEVELS
from ZZ_GROUP_MSG


        and MSG_ID = #{msgId}


        and MSG_SENDER = #{msgSender}


        and MSG_RECEIVER = #{msgReceiver}


        and SEND_TIME = #{sendTime}


        and MSG = #{msg}


        and MSG_TYPE = #{msgType}


        and MSG_PATH = #{msgPath}


        and IS_DELETE = #{isDelete}


        and IS_IMPORTANT = #{isImportant}


        and LEVELS = #{levels}

select
MSG_ID, MSG_SENDER, MSG_RECEIVER, SEND_TIME, MSG, MSG_TYPE, MSG_PATH, IS_DELETE, IS_IMPORTANT, LEVELS
from ZZ_GROUP_MSG
where 1=1

    and MSG_SENDER  = #{sender}


    and MSG_RECEIVER  = #{receiver}


    and to_char(SEND_TIME,'yyyy-mm-dd') >= #{begin_time}


    and to_char(SEND_TIME,'yyyy-mm-dd') <= #{end_time}

order by SEND_TIME desc

select
MSG_ID, MSG_SENDER, MSG_RECEIVER, SEND_TIME, MSG, MSG_TYPE, MSG_PATH, IS_DELETE, IS_IMPORTANT, LEVELS
from ZZ_GROUP_MSG_HIS
where 1=1

    and MSG_SENDER  = #{sender}


    and MSG_RECEIVER  = #{receiver}


    and to_char(SEND_TIME,'yyyy-mm-dd') >= #{begin_time}


    and to_char(SEND_TIME,'yyyy-mm-dd') <= #{end_time}

order by SEND_TIME desc

select msg_id, msg_sender, msg_receiver, send_time, msg, msg_type, msg_path, is_delete, is_important, levels from (select msg_id, msg_sender, msg_receiver, send_time, msg, msg_type, msg_path, is_delete, is_important, levels from ZZ_GROUP_MSG where 1=1
and MSG_SENDER = #{sender}
and MSG_RECEIVER = #{receiver}
and to_char(SEND_TIME,'yyyy-mm-dd') <= #{end_time}
union select msg_id, msg_sender, msg_receiver, send_time, msg, msg_type, msg_path, is_delete, is_important, levels from ZZ_GROUP_MSG_HIS where 1=1
and MSG_SENDER = #{sender}
and MSG_RECEIVER = #{receiver}
and to_char(SEND_TIME,'yyyy-mm-dd') >= #{begin_time}
) a order by a.send_time desc
